import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { login } from '../services/authService';

const Login = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await login(form);
      localStorage.setItem('token', response.token);

      // Redirect based on user role
      if (response.user && response.user.role === 'admin') {
        navigate('/admin');
      } else {
        navigate('/');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  // Quick admin login function
  const handleAdminLogin = async () => {
    setLoading(true);
    setError('');

    try {
      const adminCredentials = {
        email: 'admin@enjoydrive.com',
        password: 'admin123'
      };

      const response = await login(adminCredentials);
      localStorage.setItem('token', response.token);
      navigate('/admin');
    } catch (err) {
      setError('Admin login failed. Please check if admin account exists.');
    } finally {
      setLoading(false);
    }
  };

  // Demo user login function
  const handleDemoLogin = async () => {
    setLoading(true);
    setError('');

    try {
      const demoCredentials = {
        email: 'john@example.com',
        password: 'user123'
      };

      const response = await login(demoCredentials);
      localStorage.setItem('token', response.token);
      navigate('/');
    } catch (err) {
      setError('Demo login failed. Please check if demo account exists.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-vh-100 d-flex align-items-center justify-content-center" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 col-lg-5">
            <div className="card shadow-lg border-0" style={{ borderRadius: '1rem' }}>
              <div className="card-body p-5">
                {/* Header */}
                <div className="text-center mb-4">
                  <h1 className="h3 fw-bold text-primary mb-2">Welcome Back</h1>
                  <p className="text-muted">Sign in to your EnjoyDrive account</p>
                </div>

                {/* Error Alert */}
                {error && (
                  <div className="alert alert-danger d-flex align-items-center mb-4" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    <div>{error}</div>
                  </div>
                )}

                {/* Login Form */}
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="email" className="form-label fw-semibold">
                      <i className="fas fa-envelope me-2 text-muted"></i>Email Address
                    </label>
                    <input
                      type="email"
                      className="form-control form-control-lg"
                      id="email"
                      name="email"
                      value={form.email}
                      onChange={handleChange}
                      placeholder="Enter your email"
                      required
                      disabled={loading}
                    />
                  </div>

                  <div className="mb-4">
                    <label htmlFor="password" className="form-label fw-semibold">
                      <i className="fas fa-lock me-2 text-muted"></i>Password
                    </label>
                    <input
                      type="password"
                      className="form-control form-control-lg"
                      id="password"
                      name="password"
                      value={form.password}
                      onChange={handleChange}
                      placeholder="Enter your password"
                      required
                      disabled={loading}
                    />
                  </div>

                  <button
                    type="submit"
                    className="btn btn-primary btn-lg w-100 mb-3"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Signing In...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-sign-in-alt me-2"></i>
                        Sign In
                      </>
                    )}
                  </button>
                </form>

                {/* Divider */}
                <div className="text-center my-4">
                  <hr className="my-3" />
                  <span className="text-muted bg-white px-3">Quick Access</span>
                </div>

                {/* Admin Login Button */}
                <button
                  onClick={handleAdminLogin}
                  className="btn btn-dark btn-lg w-100 mb-3"
                  disabled={loading}
                >
                  <i className="fas fa-user-shield me-2"></i>
                  Admin Dashboard Access
                </button>

                {/* Demo Login Button */}
                <button
                  onClick={handleDemoLogin}
                  className="btn btn-outline-primary btn-lg w-100 mb-4"
                  disabled={loading}
                >
                  <i className="fas fa-user me-2"></i>
                  Demo User Access
                </button>

                {/* Demo Credentials Info */}
                <div className="card bg-light border-0 mb-4">
                  <div className="card-body p-3">
                    <h6 className="card-title mb-2">
                      <i className="fas fa-info-circle text-info me-2"></i>
                      Demo Credentials
                    </h6>
                    <div className="row">
                      <div className="col-md-6">
                        <small className="text-muted d-block">
                          <strong>Admin:</strong><br />
                          admin@enjoydrive.com<br />
                          admin123
                        </small>
                      </div>
                      <div className="col-md-6">
                        <small className="text-muted d-block">
                          <strong>User:</strong><br />
                          john@example.com<br />
                          user123
                        </small>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Register Link */}
                <div className="text-center">
                  <p className="text-muted mb-0">
                    Don't have an account?{' '}
                    <Link to="/register" className="text-primary text-decoration-none fw-semibold">
                      Create Account
                    </Link>
                  </p>
                </div>
              </div>
            </div>

            {/* Back to Home */}
            <div className="text-center mt-4">
              <Link to="/" className="text-white text-decoration-none">
                <i className="fas fa-arrow-left me-2"></i>
                Back to Home
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
