import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const VehicleList = () => {
  const [vehicles, setVehicles] = useState([]);
  const [filteredVehicles, setFilteredVehicles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeFilter, setActiveFilter] = useState('All');
  const navigate = useNavigate();

  // Mock data for now (will be replaced with API call)
  const mockVehicles = [
    {
      _id: '1',
      name: 'Mercedes-Benz S-Class',
      category: 'luxury',
      description: 'Experience ultimate luxury with our premium Mercedes-Benz S-Class.',
      pricePerDay: 299,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1563720223185-11003d516935?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '2',
      name: 'BMW M4 Coupe',
      category: 'sports',
      description: 'Feel the thrill of driving with our high-performance BMW M4 Coupe.',
      pricePerDay: 399,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1555215695-3004980ad54e?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '3',
      name: 'Range Rover Evoque',
      category: 'suv',
      description: 'Conquer any terrain with style in our luxurious Range Rover Evoque.',
      pricePerDay: 249,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1606664515524-ed2f786a0bd6?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '4',
      name: 'Tesla Model S',
      category: 'luxury',
      description: 'Experience the future of driving with our Tesla Model S.',
      pricePerDay: 349,
      year: 2023,
      status: 'rented',
      image: 'https://images.unsplash.com/photo-1560958089-b8a1929cea89?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '5',
      name: 'Audi A4 Sedan',
      category: 'luxury',
      description: 'Elegant and efficient, our Audi A4 offers the perfect balance of luxury and practicality.',
      pricePerDay: 179,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1606664515524-ed2f786a0bd6?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '6',
      name: 'Porsche 911 Carrera',
      category: 'sports',
      description: 'The iconic sports car that defines performance.',
      pricePerDay: 499,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1544636331-e26879cd4d9b?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '7',
      name: 'Harley-Davidson Street 750',
      category: 'bike',
      description: 'Classic American cruiser bike perfect for city rides and weekend adventures.',
      pricePerDay: 89,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '8',
      name: 'Yamaha YZF-R1',
      category: 'bike',
      description: 'High-performance sports bike for thrill seekers and racing enthusiasts.',
      pricePerDay: 149,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '9',
      name: 'BMW X5',
      category: 'suv',
      description: 'Premium luxury SUV with exceptional comfort and performance.',
      pricePerDay: 329,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1606664515524-ed2f786a0bd6?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '10',
      name: 'Honda CBR600RR',
      category: 'bike',
      description: 'Reliable sports bike with excellent handling and fuel efficiency.',
      pricePerDay: 99,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '11',
      name: 'Ducati Panigale V4',
      category: 'bike',
      description: 'Italian superbike engineering at its finest with unmatched performance.',
      pricePerDay: 199,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '12',
      name: 'Rolls-Royce Phantom',
      category: 'luxury',
      description: 'The ultimate in luxury and prestige for special occasions.',
      pricePerDay: 1299,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1563720223185-11003d516935?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '13',
      name: 'Kawasaki Ninja ZX-10R',
      category: 'bike',
      description: 'Track-ready superbike with cutting-edge technology and speed.',
      pricePerDay: 179,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '14',
      name: 'Royal Enfield Classic 350',
      category: 'bike',
      description: 'Vintage-style motorcycle perfect for leisurely rides and touring.',
      pricePerDay: 69,
      year: 2023,
      status: 'available',
      image: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    },
    {
      _id: '15',
      name: 'Suzuki GSX-R1000',
      category: 'bike',
      description: 'Legendary superbike with exceptional power and agility.',
      pricePerDay: 159,
      year: 2023,
      status: 'rented',
      image: 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    }
  ];

  // Filter vehicles based on active filter
  const filterVehicles = (filter) => {
    if (filter === 'All') {
      setFilteredVehicles(vehicles);
    } else {
      const filtered = vehicles.filter(vehicle =>
        vehicle.category.toLowerCase() === filter.toLowerCase()
      );
      setFilteredVehicles(filtered);
    }
    setActiveFilter(filter);
  };

  // Handle Book Now button click
  const handleBookNow = (vehicleId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      // Redirect to login if not authenticated
      navigate('/login');
      return;
    }
    // Navigate to booking page with vehicle ID
    navigate(`/book/${vehicleId}`);
  };

  // Handle Details button click
  const handleViewDetails = (vehicleId) => {
    navigate(`/vehicles/${vehicleId}`);
  };

  useEffect(() => {
    // Simulate API call
    const fetchVehicles = async () => {
      try {
        setLoading(true);
        // Try to fetch from API first
        const response = await fetch('/api/vehicles');
        if (response.ok) {
          const data = await response.json();
          setVehicles(data);
          setFilteredVehicles(data);
        } else {
          // Fallback to mock data
          setVehicles(mockVehicles);
          setFilteredVehicles(mockVehicles);
        }
      } catch (err) {
        // Fallback to mock data on error
        setVehicles(mockVehicles);
        setFilteredVehicles(mockVehicles);
        console.log('Using mock data:', err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  // Update filtered vehicles when vehicles change
  useEffect(() => {
    filterVehicles(activeFilter);
  }, [vehicles]);

  if (loading) {
    return (
      <section className="vehicles-section">
        <div className="container">
          <div className="loading">
            <div className="spinner"></div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="vehicles-section">
      <div className="container">
        <h2 className="section-title">Our Collection</h2>
        <p className="section-subtitle">Explore our collection of premium vehicles and bikes</p>

        {/* Filter Buttons */}
        <div className="filter-buttons">
          {['All', 'Luxury', 'Sports', 'SUV', 'Bike'].map((filter) => (
            <button
              key={filter}
              className={`filter-btn ${activeFilter === filter ? 'active' : ''}`}
              onClick={() => filterVehicles(filter)}
            >
              {filter}
            </button>
          ))}
        </div>

        <div className="vehicles-grid">
          {filteredVehicles.map((vehicle) => (
            <div key={vehicle._id} className="vehicle-card">
              <div className="vehicle-image-container">
                <img
                  src={vehicle.image}
                  alt={vehicle.name}
                  className="vehicle-image"
                />
                <div className="vehicle-price-overlay">
                  <div className="price-tag">
                    ${vehicle.pricePerDay}/day
                  </div>
                </div>
              </div>
              <div className="vehicle-info">
                <h3 className="vehicle-name">{vehicle.name}</h3>
                <p className="vehicle-description">{vehicle.description}</p>
                <div style={{ marginBottom: '1rem' }}>
                  <span className={`status-${vehicle.status}`}>
                    {vehicle.status === 'available' ? 'Available' : 'Rented'}
                  </span>
                </div>
                <div className="vehicle-actions">
                  <button
                    className="btn btn-primary"
                    disabled={vehicle.status !== 'available'}
                    onClick={() => handleBookNow(vehicle._id)}
                  >
                    {vehicle.status === 'available' ? 'Book Now' : 'Not Available'}
                  </button>
                  <button
                    className="btn btn-secondary"
                    onClick={() => handleViewDetails(vehicle._id)}
                  >
                    Details
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default VehicleList;
