import React, { useState, useEffect } from 'react';

const Testimonials = () => {
  const [testimonials, setTestimonials] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [loading, setLoading] = useState(true);

  // Default testimonials as fallback
  const defaultTestimonials = [
    {
      _id: '1',
      name: 'James Wilson',
      role: 'Business Executive',
      image: 'https://res.cloudinary.com/dz7pl7mlf/image/upload/v1745251679/360_F_243123463_zTooub557xEWABDLk0jJklDyLSGl2jrr_ejkby9.jpg',
      quote: 'Mercedes-Benz vehicles consistently receive positive reviews, particularly for their luxury interiors, stylish designs, and advanced technology features.',
      rating: 5
    },
    {
      _id: '2',
      name: 'Michael Roberts',
      role: 'Car Enthusiast',
      image: 'https://res.cloudinary.com/dz7pl7mlf/image/upload/v1745251787/rsflbsvuxsexcdiw5vss.jpg',
      quote: 'Toyota is best car according to the budget and comfort it gives nice mileage and is fully loaded with features. Looks of the car is awesome. The pickup of the car is very nice, performance of the car is great.',
      rating: 4
    },
    {
      _id: '3',
      name: 'Sarah Chen',
      role: 'Wedding Planner',
      image: 'https://res.cloudinary.com/dz7pl7mlf/image/upload/v1745251929/yzpwprwge2msh67wmvzo.jpg',
      quote: 'As a car enthusiast, I was blown away by their collection. The Ferrari 488 was perfectly maintained and the team\'s knowledge about the vehicles is impressive. Will definitely rent again!',
      rating: 5
    }
  ];

  useEffect(() => {
    fetchTestimonials();
  }, []);

  const fetchTestimonials = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/testimonials');

      if (response.ok) {
        const data = await response.json();
        if (data.length > 0) {
          setTestimonials(data);
        } else {
          setTestimonials(defaultTestimonials);
        }
      } else {
        setTestimonials(defaultTestimonials);
      }
    } catch (error) {
      console.error('Error fetching testimonials:', error);
      setTestimonials(defaultTestimonials);
    } finally {
      setLoading(false);
    }
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <i
        key={i}
        className={`fas fa-star ${i < rating ? 'text-warning' : 'text-muted'}`}
        style={{ fontSize: '0.9rem' }}
      ></i>
    ));
  };

  const nextTestimonial = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === testimonials.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevTestimonial = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? testimonials.length - 1 : prevIndex - 1
    );
  };

  if (loading) {
    return (
      <section id="testimonials" className="testimonials py-5 bg-light">
        <div className="container">
          <div className="section-header text-center mb-4">
            <h2>Client Reviews</h2>
            <p>What our customers say about their experience</p>
          </div>
          <div className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        </div>
      </section>
    );
  }

  const currentTestimonial = testimonials[currentIndex];

  return (
    <section id="testimonials" className="testimonials py-5 bg-light">
      <div className="container">
        <div className="section-header text-center mb-4">
          <h2>Client Reviews</h2>
          <p>What our customers say about their experience</p>
        </div>

        {/* Single testimonial with navigation */}
        <div className="row justify-content-center">
          <div className="col-md-8">
            <div className="testimonial-carousel position-relative">
              {/* Navigation buttons */}
              <button
                className="btn btn-primary rounded-circle position-absolute start-0 top-50 translate-middle-y"
                style={{ zIndex: 10, width: '50px', height: '50px' }}
                onClick={prevTestimonial}
              >
                <i className="fas fa-chevron-left"></i>
              </button>

              <button
                className="btn btn-primary rounded-circle position-absolute end-0 top-50 translate-middle-y"
                style={{ zIndex: 10, width: '50px', height: '50px' }}
                onClick={nextTestimonial}
              >
                <i className="fas fa-chevron-right"></i>
              </button>

              {/* Testimonial content */}
              <div className="testimonial-box card p-5 text-center shadow-sm mx-5">
                <img
                  src={currentTestimonial.image}
                  className="testimonial-image rounded-circle mb-4 mx-auto"
                  alt={currentTestimonial.name}
                  width="100"
                  height="100"
                  style={{ objectFit: 'cover' }}
                />
                <blockquote className="blockquote mb-4">
                  <p className="lead">"{currentTestimonial.quote}"</p>
                </blockquote>
                <div className="mb-2">
                  {renderStars(currentTestimonial.rating)}
                </div>
                <footer className="blockquote-footer">
                  <strong>{currentTestimonial.name}</strong>, {currentTestimonial.role}
                </footer>
              </div>
            </div>

            {/* Dots indicator */}
            <div className="text-center mt-4">
              {testimonials.map((_, index) => (
                <button
                  key={index}
                  className={`btn btn-sm rounded-circle me-2 ${
                    index === currentIndex ? 'btn-primary' : 'btn-outline-primary'
                  }`}
                  style={{ width: '12px', height: '12px', padding: 0 }}
                  onClick={() => setCurrentIndex(index)}
                ></button>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
