import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
      // In a real app, you'd decode the token to get user role
      // For now, we'll check if user can access admin routes
      checkUserRole();
    }
  }, []);

  const checkUserRole = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const userData = await response.json();
        setUserRole(userData.role);
      }
    } catch (error) {
      console.error('Error checking user role:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUserRole(null);
    navigate('/');
  };

  const handleAdminAccess = () => {
    if (userRole === 'admin') {
      navigate('/admin');
    } else {
      navigate('/login');
    }
  };

  return (
    <header className="header sticky-top bg-white shadow-sm">
      <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light py-3">
          {/* Brand Logo */}
          <Link to="/" className="navbar-brand fw-bold fs-3 text-primary">
            <i className="fas fa-car me-2"></i>
            EnjoyDrive
          </Link>

          {/* Mobile Menu Toggle */}
          <button
            className="navbar-toggler border-0"
            type="button"
            onClick={() => setShowMobileMenu(!showMobileMenu)}
            aria-expanded={showMobileMenu}
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          {/* Navigation Menu */}
          <div className={`collapse navbar-collapse ${showMobileMenu ? 'show' : ''}`}>
            <ul className="navbar-nav mx-auto">
              <li className="nav-item">
                <Link to="/" className="nav-link fw-semibold">
                  <i className="fas fa-home me-1"></i>Home
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/#vehicles" className="nav-link fw-semibold">
                  <i className="fas fa-car me-1"></i>Vehicles
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/#testimonials" className="nav-link fw-semibold">
                  <i className="fas fa-star me-1"></i>Reviews
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/#contact" className="nav-link fw-semibold">
                  <i className="fas fa-envelope me-1"></i>Contact
                </Link>
              </li>
            </ul>

            {/* Auth Buttons */}
            <div className="d-flex align-items-center gap-2">
              {isLoggedIn ? (
                <>
                  {userRole === 'admin' && (
                    <button
                      onClick={handleAdminAccess}
                      className="btn btn-dark btn-sm me-2"
                      title="Admin Dashboard"
                    >
                      <i className="fas fa-user-shield me-1"></i>
                      Admin
                    </button>
                  )}
                  <div className="dropdown">
                    <button
                      className="btn btn-outline-primary dropdown-toggle"
                      type="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                    >
                      <i className="fas fa-user me-1"></i>
                      Account
                    </button>
                    <ul className="dropdown-menu">
                      <li>
                        <Link to="/profile" className="dropdown-item">
                          <i className="fas fa-user-circle me-2"></i>Profile
                        </Link>
                      </li>
                      <li>
                        <Link to="/bookings" className="dropdown-item">
                          <i className="fas fa-calendar-alt me-2"></i>My Bookings
                        </Link>
                      </li>
                      <li><hr className="dropdown-divider" /></li>
                      <li>
                        <button onClick={handleLogout} className="dropdown-item text-danger">
                          <i className="fas fa-sign-out-alt me-2"></i>Logout
                        </button>
                      </li>
                    </ul>
                  </div>
                </>
              ) : (
                <>
                  <Link to="/login" className="btn btn-outline-primary me-2">
                    <i className="fas fa-sign-in-alt me-1"></i>Login
                  </Link>
                  <Link to="/register" className="btn btn-primary">
                    <i className="fas fa-user-plus me-1"></i>Sign Up
                  </Link>

                  {/* Admin Quick Access */}
                  <div className="dropdown ms-2">
                    <button
                      className="btn btn-dark btn-sm dropdown-toggle"
                      type="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                      title="Admin Access"
                    >
                      <i className="fas fa-user-shield"></i>
                    </button>
                    <ul className="dropdown-menu dropdown-menu-end">
                      <li>
                        <h6 className="dropdown-header">
                          <i className="fas fa-shield-alt me-2"></i>Admin Access
                        </h6>
                      </li>
                      <li>
                        <Link to="/login" className="dropdown-item">
                          <i className="fas fa-sign-in-alt me-2"></i>Admin Login
                        </Link>
                      </li>
                      <li><hr className="dropdown-divider" /></li>
                      <li>
                        <div className="px-3 py-2">
                          <small className="text-muted">
                            <strong>Demo Admin:</strong><br />
                            admin@enjoydrive.com<br />
                            admin123
                          </small>
                        </div>
                      </li>
                    </ul>
                  </div>
                </>
              )}
            </div>
          </div>
        </nav>
      </div>
    </header>
  );
};

export default Navbar;
