import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { login } from '../services/authService';

const AdminLogin = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await login(form);

      // Check if user is admin
      if (response.user && response.user.role === 'admin') {
        localStorage.setItem('token', response.token);
        navigate('/admin');
      } else {
        setError('Access denied. Admin credentials required.');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  // Quick admin login function
  const handleQuickAdminLogin = async () => {
    setLoading(true);
    setError('');

    try {
      const adminCredentials = {
        email: 'admin@enjoydrive.com',
        password: 'admin123'
      };

      const response = await login(adminCredentials);
      localStorage.setItem('token', response.token);
      navigate('/admin');
    } catch (err) {
      setError('Admin login failed. Please check if admin account exists.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-vh-100 d-flex align-items-center justify-content-center" style={{ 
      background: 'linear-gradient(135deg, #2c3e50 0%, #34495e 100%)',
      position: 'relative'
    }}>
      {/* Background Pattern */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.03'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
        opacity: 0.5
      }}></div>

      <div className="container position-relative">
        <div className="row justify-content-center">
          <div className="col-md-5 col-lg-4">
            <div className="card shadow-lg border-0" style={{ 
              borderRadius: '1rem',
              background: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(10px)'
            }}>
              <div className="card-body p-5">
                {/* Header */}
                <div className="text-center mb-4">
                  <div className="mb-3">
                    <i className="fas fa-shield-alt text-dark" style={{ fontSize: '3rem' }}></i>
                  </div>
                  <h1 className="h3 fw-bold text-dark mb-2">Admin Login</h1>
                  <p className="text-muted">Access the administrative dashboard</p>
                </div>

                {/* Error Alert */}
                {error && (
                  <div className="alert alert-danger d-flex align-items-center mb-4" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    <div>{error}</div>
                  </div>
                )}

                {/* Login Form */}
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="email" className="form-label fw-semibold text-dark">
                      <i className="fas fa-user me-2 text-muted"></i>Username
                    </label>
                    <input
                      type="email"
                      className="form-control form-control-lg"
                      id="email"
                      name="email"
                      value={form.email}
                      onChange={handleChange}
                      placeholder="Enter admin email"
                      required
                      disabled={loading}
                      style={{ 
                        borderRadius: '0.5rem',
                        border: '2px solid #e9ecef',
                        fontSize: '1rem'
                      }}
                    />
                  </div>

                  <div className="mb-4">
                    <label htmlFor="password" className="form-label fw-semibold text-dark">
                      <i className="fas fa-lock me-2 text-muted"></i>Password
                    </label>
                    <input
                      type="password"
                      className="form-control form-control-lg"
                      id="password"
                      name="password"
                      value={form.password}
                      onChange={handleChange}
                      placeholder="Enter admin password"
                      required
                      disabled={loading}
                      style={{ 
                        borderRadius: '0.5rem',
                        border: '2px solid #e9ecef',
                        fontSize: '1rem'
                      }}
                    />
                  </div>

                  <button
                    type="submit"
                    className="btn btn-lg w-100 mb-3"
                    disabled={loading}
                    style={{
                      background: 'linear-gradient(135deg, #2c3e50 0%, #34495e 100%)',
                      border: 'none',
                      borderRadius: '0.5rem',
                      color: 'white',
                      fontWeight: '600',
                      padding: '12px'
                    }}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Authenticating...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-sign-in-alt me-2"></i>
                        Login
                      </>
                    )}
                  </button>
                </form>

                {/* Quick Access */}
                <div className="text-center my-4">
                  <hr className="my-3" />
                  <span className="text-muted bg-white px-3" style={{ fontSize: '0.9rem' }}>Quick Access</span>
                </div>

                {/* Demo Admin Login Button */}
                <button
                  onClick={handleQuickAdminLogin}
                  className="btn btn-outline-dark btn-lg w-100 mb-4"
                  disabled={loading}
                  style={{ borderRadius: '0.5rem', fontWeight: '600' }}
                >
                  <i className="fas fa-user-shield me-2"></i>
                  Demo Admin Login
                </button>

                {/* Demo Credentials Info */}
                <div className="card bg-light border-0 mb-4" style={{ borderRadius: '0.5rem' }}>
                  <div className="card-body p-3">
                    <h6 className="card-title mb-2 text-center">
                      <i className="fas fa-info-circle text-info me-2"></i>
                      Demo Admin Credentials
                    </h6>
                    <div className="text-center">
                      <small className="text-muted d-block">
                        <strong>Email:</strong> admin@enjoydrive.com<br />
                        <strong>Password:</strong> admin123
                      </small>
                    </div>
                  </div>
                </div>

                {/* Back to Site */}
                <div className="text-center">
                  <Link to="/" className="text-muted text-decoration-none">
                    <i className="fas fa-arrow-left me-2"></i>
                    Back to Site
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
